from sklearn.metrics import classification_report
import matplotlib.pyplot as plt
import numpy as np
import seaborn as sns

print(len(predicted))
print(len(gold_labels))

def get_macro_label(micro_labels):
    if "REFUTES" in micro_labels and "SUPPORTS" in micro_labels:
        return "DISPUTED"
    elif "REFUTES" in micro_labels:
        return "REFUTES"
    elif "SUPPORTS" in micro_labels:
        return "SUPPORTS"
    else:
        return "NOT_ENOUGH_INFO"

def get_macro_labels_from_micro(micro_labels):
  macro_label_predictions=[]
  for i in range(0, len(micro_labels), 5):
    micro_labels_for_claim = micro_labels[i:i+5]
    macro_label_prediction = get_macro_label(micro_labels_for_claim)
    macro_label_predictions.append(macro_label_prediction)
  return macro_label_predictions

gold_macro = get_macro_labels_from_micro(gold_labels)

predicted_macro = get_macro_labels_from_micro(predicted)


print("MICRO PREDICTIONS")
print(classification_report(predicted, gold_labels, digits=3))

print("MACRO PREDICTIONS")
print(classification_report(predicted_macro, gold_macro, digits=3))

import matplotlib.pyplot as plt
import numpy as np
import seaborn as sns

def plot_confusion_matrix(y_true, y_pred, labels):
    confusion_matrix = np.zeros((len(labels), len(labels)), dtype=int)
    for true_label, pred_label in zip(y_true, y_pred):
        confusion_matrix[labels.index(true_label)][labels.index(pred_label)] += 1
    
    plt.figure(figsize=(10, 8))
    sns.heatmap(confusion_matrix, annot=True, fmt="d", cmap="Blues", xticklabels=labels, yticklabels=labels, cbar=False)
    plt.xlabel("Predicted Labels", fontsize=12)
    plt.ylabel("True Labels", fontsize=12)
    plt.title("Confusion Matrix", fontsize=14)
    plt.xticks(fontsize=10)
    plt.yticks(fontsize=10)
    plt.show()

plot_confusion_matrix(gold_labels, predicted, ['REFUTES', 'NOT_ENOUGH_INFO', 'SUPPORTS'])
plot_confusion_matrix(gold_macro, predicted_macro, ['REFUTES', 'NOT_ENOUGH_INFO', 'SUPPORTS', 'DISPUTED'])